import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import { useNavigate } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function ProfitLossPage() {
  const [records, setRecords] = useState([]);
  const [formData, setFormData] = useState({
    year: "",
    crop: "",
    yield: "",
    cost: "",
    pricePerTon: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    const { year, crop, yield: y, cost, pricePerTon } = formData;
    if (!year || !crop || !y || !cost || !pricePerTon) {
      alert("Please fill all fields!");
      return;
    }

    const yieldValue = parseFloat(y);
    const price = parseFloat(pricePerTon);
    const costValue = parseFloat(cost);
    const revenue = yieldValue * price;
    const profit = revenue - costValue;

    const newRecord = { ...formData, revenue, profit };
    setRecords([...records, newRecord]);
    setFormData({ year: "", crop: "", yield: "", cost: "", pricePerTon: "" });
  };

  const totalProfit = records.reduce((acc, rec) => acc + rec.profit, 0);

  const chartData = {
    labels: records.map((r) => r.year),
    datasets: [
      {
        label: "Profit (‚Çπ)",
        data: records.map((r) => r.profit),
        backgroundColor: records.map((r) =>
          r.profit >= 0
            ? "rgba(102, 187, 106, 0.8)"
            : "rgba(239, 83, 80, 0.8)"
        ),
        borderRadius: 10,
      },
    ],
  };

  return (
    <div style={styles.pageWrapper}>
      <div style={styles.card}>
        <h2 style={styles.heading}>üíπ Profit‚ÄìLoss Dashboard</h2>
        <p style={styles.subHeading}>
          Track your yearly crop performance with an interactive visualization üåæ
        </p>

        <form onSubmit={handleAdd} style={styles.form}>
          {["year", "crop", "yield", "cost", "pricePerTon"].map((field, i) => (
            <input
              key={i}
              type={field === "crop" ? "text" : "number"}
              step="0.01"
              name={field}
              placeholder={
                field === "year"
                  ? "Year"
                  : field === "yield"
                  ? "Yield (tons)"
                  : field === "cost"
                  ? "Total Cost (‚Çπ)"
                  : field === "pricePerTon"
                  ? "Price per Ton (‚Çπ)"
                  : field
              }
              value={formData[field]}
              onChange={handleChange}
              style={styles.input}
            />
          ))}
          <button type="submit" style={styles.addBtn}>
            ‚ûï Add Record
          </button>
        </form>

        {records.length > 0 ? (
          <>
            <div style={styles.chartBox}>
              <Bar data={chartData} />
            </div>

            <h3 style={styles.totalProfit}>
              Total Profit: <span>‚Çπ{totalProfit.toFixed(2)}</span>
            </h3>

            <button
              onClick={() => navigate("/rewards", { state: { totalProfit } })}
              style={styles.rewardBtn}
            >
              üéÅ View My Rewards
            </button>

            <div style={styles.tableContainer}>
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th>Year</th>
                    <th>Crop</th>
                    <th>Yield (tons)</th>
                    <th>Revenue (‚Çπ)</th>
                    <th>Cost (‚Çπ)</th>
                    <th>Profit/Loss (‚Çπ)</th>
                  </tr>
                </thead>
                <tbody>
                  {records.map((r, i) => (
                    <tr key={i}>
                      <td>{r.year}</td>
                      <td>{r.crop}</td>
                      <td>{r.yield}</td>
                      <td>{r.revenue}</td>
                      <td>{r.cost}</td>
                      <td
                        style={{
                          color: r.profit >= 0 ? "#2E7D32" : "#E53935",
                          fontWeight: "bold",
                        }}
                      >
                        {r.profit}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        ) : (
          <p style={styles.noData}>No records added yet. Start by adding one! üå±</p>
        )}
      </div>
    </div>
  );
}

const styles = {
  pageWrapper: {
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    minHeight: "100vh",
    background: "linear-gradient(135deg, #E8F5E9, #F1F8E9)",
    padding: "60px 20px",
  },
  card: {
    background: "linear-gradient(180deg, #ffffff, #F9FFF9)",
    width: "90%",
    maxWidth: "1000px",
    padding: "40px 50px",
    borderRadius: "20px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.15)",
    textAlign: "center",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
    minHeight: "85vh",
  },
  heading: {
    color: "#2E7D32",
    fontSize: "30px",
    fontWeight: "700",
    marginBottom: "10px",
    fontFamily: "'Poppins', sans-serif",
  },
  subHeading: {
    color: "#555",
    fontSize: "15px",
    marginBottom: "25px",
    fontFamily: "'Poppins', sans-serif",
  },
  form: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(160px, 1fr))",
    gap: "15px",
    marginBottom: "30px",
  },
  input: {
    padding: "12px 10px",
    borderRadius: "10px",
    border: "1px solid #A5D6A7",
    outline: "none",
    transition: "0.3s",
    fontFamily: "'Poppins', sans-serif",
    backgroundColor: "#FAFAFA",
  },
  addBtn: {
    gridColumn: "1 / -1",
    padding: "12px",
    background: "linear-gradient(90deg, #66BB6A, #81C784)",
    color: "white",
    fontWeight: "bold",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    transition: "0.3s ease",
  },
  chartBox: {
    background: "#F5FFF6",
    padding: "25px",
    borderRadius: "16px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.08)",
    marginTop: "15px",
  },
  totalProfit: {
    marginTop: "25px",
    fontSize: "22px",
    color: "#1B5E20",
    fontWeight: "600",
  },
  rewardBtn: {
    marginTop: "25px",
    background: "linear-gradient(90deg, #FFB74D, #FFA726)",
    color: "white",
    padding: "14px 25px",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    fontSize: "17px",
    fontWeight: "bold",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
  },
  tableContainer: {
    marginTop: "35px",
    overflowX: "auto",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    borderRadius: "10px",
    fontFamily: "'Poppins', sans-serif",
    backgroundColor: "#FFFFFF",
  },
  noData: {
    color: "#777",
    marginTop: "30px",
    fontSize: "16px",
  },
};

export default ProfitLossPage;
