// src/ProfitLossPage.js
import React from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
  Title,
} from "chart.js";

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend, Title);

function ProfitLossPage() {
  // Example dummy data (you can later fetch this from backend)
  const data = [
    { year: 2021, yield: 2.8, profit: 15000 },
    { year: 2022, yield: 3.5, profit: 21000 },
    { year: 2023, yield: 3.2, profit: 18500 },
    { year: 2024, yield: 3.8, profit: 24000 },
  ];

  // Calculate profit/loss %
  const profitChange = data.map((item, index) => {
    if (index === 0) return 0;
    const change = ((item.profit - data[index - 1].profit) / data[index - 1].profit) * 100;
    return parseFloat(change.toFixed(2));
  });

  const chartData = {
    labels: data.map((d) => d.year),
    datasets: [
      {
        label: "Profit (â‚¹)",
        data: data.map((d) => d.profit),
        backgroundColor: "rgba(75, 192, 192, 0.6)",
      },
      {
        label: "Profit Change (%)",
        data: profitChange,
        backgroundColor: "rgba(255, 99, 132, 0.6)",
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: "Yearly Profitâ€“Loss Comparison",
        font: { size: 18 },
      },
      legend: { position: "top" },
    },
  };

  return (
    <div style={{ maxWidth: "800px", margin: "50px auto", textAlign: "center" }}>
      <h2>ðŸ“Š Profitâ€“Loss Analysis</h2>
      <p>Compare yearly profits and visualize percentage change trends.</p>
      <div style={{ background: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 2px 6px rgba(0,0,0,0.1)" }}>
        <Bar data={chartData} options={options} />
      </div>
    </div>
  );
}

export default ProfitLossPage;
