import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import { useNavigate } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function ProfitLossPage() {
  const [records, setRecords] = useState([]);
  const [formData, setFormData] = useState({
    year: "",
    crop: "",
    yield: "",
    cost: "",
    pricePerTon: "",
  });
  const [animate, setAnimate] = useState(false); // <-- animation trigger

  const navigate = useNavigate();

  useEffect(() => {
    // Trigger animations after mount
    setAnimate(true);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    const { year, crop, yield: y, cost, pricePerTon } = formData;

    if (!year || !crop || !y || !cost || !pricePerTon) {
      alert("Please fill all fields!");
      return;
    }

    const yieldValue = parseFloat(y);
    const price = parseFloat(pricePerTon);
    const costValue = parseFloat(cost);
    const revenue = yieldValue * price;
    const profit = revenue - costValue;

    const newRecord = { ...formData, revenue, profit };
    setRecords([...records, newRecord]);
    setFormData({ year: "", crop: "", yield: "", cost: "", pricePerTon: "" });
  };

  const totalProfit = records.reduce((acc, rec) => acc + rec.profit, 0);

  const chartData = {
    labels: records.map((r) => r.year),
    datasets: [
      {
        label: "Profit (‚Çπ)",
        data: records.map((r) => r.profit),
        backgroundColor: records.map((r) =>
          r.profit >= 0 ? "rgba(76, 175, 80, 0.7)" : "rgba(244, 67, 54, 0.7)"
        ),
        borderRadius: 10,
      },
    ],
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>üåæ Profit‚ÄìLoss Analysis</h2>

      <form onSubmit={handleAdd} style={styles.form}>
        {["year", "crop", "yield", "cost", "pricePerTon"].map((field, i) => (
          <input
            key={i}
            type={field === "crop" ? "text" : "number"}
            step="0.01"
            name={field}
            placeholder={
              field === "year"
                ? "Year"
                : field === "yield"
                ? "Yield (tons)"
                : field === "cost"
                ? "Total Cost (‚Çπ)"
                : field === "pricePerTon"
                ? "Price per Ton (‚Çπ)"
                : field
            }
            value={formData[field]}
            onChange={handleChange}
            style={styles.input}
          />
        ))}
        <button type="submit" style={styles.addBtn}>
          ‚ûï Add Record
        </button>
      </form>

      {records.length > 0 ? (
        <>
          <div
            style={{
              ...styles.chartCard,
              transform: animate ? "translateY(0)" : "translateY(30px)",
              opacity: animate ? 1 : 0,
              transition: "all 0.6s ease",
            }}
          >
            <Bar data={chartData} />
          </div>

          <h3
            style={{
              ...styles.totalProfit,
              transform: animate ? "translateY(0)" : "translateY(30px)",
              opacity: animate ? 1 : 0,
              transition: "all 0.7s ease",
            }}
          >
            Total Profit: <span>‚Çπ{totalProfit.toFixed(2)}</span>
          </h3>

          <button
            onClick={() => navigate("/rewards", { state: { totalProfit } })}
            style={{
              ...styles.rewardBtn,
              transform: animate ? "translateY(0)" : "translateY(30px)",
              opacity: animate ? 1 : 0,
              transition: "all 0.8s ease",
            }}
          >
            üéÅ View My Rewards
          </button>

          <div
            style={{
              ...styles.tableWrapper,
              transform: animate ? "translateY(0)" : "translateY(30px)",
              opacity: animate ? 1 : 0,
              transition: "all 0.9s ease",
            }}
          >
            <table style={styles.table}>
              <thead>
                <tr style={styles.tableHeadRow}>
                  <th>Year</th>
                  <th>Crop</th>
                  <th>Yield (tons)</th>
                  <th>Revenue (‚Çπ)</th>
                  <th>Cost (‚Çπ)</th>
                  <th>Profit/Loss (‚Çπ)</th>
                </tr>
              </thead>
              <tbody>
                {records.map((r, i) => (
                  <tr
                    key={i}
                    style={{
                      ...styles.tableRow,
                      transform: animate ? "translateX(0)" : "translateX(-50px)",
                      opacity: animate ? 1 : 0,
                      transition: `all 0.5s ease ${i * 0.1}s`, // staggered
                    }}
                  >
                    <td>{r.year}</td>
                    <td>{r.crop}</td>
                    <td>{r.yield}</td>
                    <td>{r.revenue}</td>
                    <td>{r.cost}</td>
                    <td style={{ color: r.profit >= 0 ? "#2E7D32" : "#C62828" }}>
                      {r.profit}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      ) : (
        <p style={{ marginTop: "20px", color: "#555" }}>
          No records added yet. Start by adding one! üå±
        </p>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "1000px",
    minHeight: "30vh",
    margin: "50px auto",
    padding: "50px",
    background: "linear-gradient(180deg, #E8F5E9 0%, #FFFFFF 100%)",
    borderRadius: "20px",
    boxShadow: "0 8px 20px rgba(0,0,0,0.1)",
    fontFamily: "'Poppins', sans-serif",
  },
  heading: {
    color: "#1B5E20",
    fontSize: "28px",
    fontWeight: "700",
    marginBottom: "25px",
  },
  form: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(160px, 1fr))",
    gap: "15px",
    marginBottom: "30px",
  },
  input: {
    padding: "12px",
    borderRadius: "10px",
    border: "1.5px solid #A5D6A7",
    background: "#F9FFF9",
    transition: "0.3s",
  },
  addBtn: {
    gridColumn: "1 / -1",
    padding: "14px",
    background: "linear-gradient(90deg, #43A047, #66BB6A)",
    color: "white",
    fontWeight: "bold",
    border: "none",
    borderRadius: "12px",
    cursor: "pointer",
    fontSize: "16px",
    transition: "0.3s ease",
  },
  chartCard: {
    background: "white",
    padding: "25px",
    borderRadius: "14px",
    boxShadow: "0 6px 15px rgba(0,0,0,0.08)",
  },
  totalProfit: {
    marginTop: "30px",
    color: "#2E7D32",
    fontSize: "22px",
    fontWeight: "700",
  },
  rewardBtn: {
    marginTop: "25px",
    background: "linear-gradient(90deg, #66BB6A, #43A047)",
    color: "white",
    padding: "14px 30px",
    border: "none",
    borderRadius: "12px",
    cursor: "pointer",
    fontSize: "17px",
    fontWeight: "600",
    transition: "all 0.3s ease",
  },
  tableWrapper: {
    marginTop: "35px",
    overflowX: "auto",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
  },
  tableHeadRow: {
    background: "#C8E6C9",
    color: "#1B5E20",
  },
  tableRow: {
    background: "#F1F8E9",
  },
};

export default ProfitLossPage;
