import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function ProfitLossPage() {
  const [records, setRecords] = useState([]);
  const [formData, setFormData] = useState({
    year: "",
    crop: "",
    yield: "",
    cost: "",
    pricePerTon: "",
  });

  // Handle change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Add record
  const handleAdd = (e) => {
    e.preventDefault();
    if (!formData.year || !formData.crop || !formData.yield || !formData.cost || !formData.pricePerTon) {
      alert("Please fill all fields!");
      return;
    }

    const yieldValue = parseFloat(formData.yield);
    const price = parseFloat(formData.pricePerTon);
    const cost = parseFloat(formData.cost);
    const revenue = yieldValue * price;
    const profit = revenue - cost;

    const newRecord = {
      ...formData,
      revenue,
      profit,
    };

    setRecords([...records, newRecord]);
    setFormData({ year: "", crop: "", yield: "", cost: "", pricePerTon: "" });
  };

  // Prepare chart data
  const chartData = {
    labels: records.map((r) => r.year),
    datasets: [
      {
        label: "Profit (â‚¹)",
        data: records.map((r) => r.profit),
        backgroundColor: records.map((r) =>
          r.profit >= 0 ? "rgba(76, 175, 80, 0.7)" : "rgba(244, 67, 54, 0.7)"
        ),
      },
    ],
  };

  return (
    <div style={{ maxWidth: "800px", margin: "50px auto", textAlign: "center" }}>
      <h2>ðŸ’¹ Profitâ€“Loss Analysis</h2>

      <form
  onSubmit={handleAdd}
  style={{
    backgroundColor: "#f8f9fa",
    padding: "25px",
    borderRadius: "12px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(180px, 1fr))",
    gap: "15px",
    alignItems: "center",
    marginBottom: "30px",
  }}
>
  <input
    type="number"
    name="year"
    placeholder="ðŸ“… Year"
    value={formData.year}
    onChange={handleChange}
    style={inputStyle}
  />
  <input
    type="text"
    name="crop"
    placeholder="ðŸŒ¾ Crop"
    value={formData.crop}
    onChange={handleChange}
    style={inputStyle}
  />
  <input
    type="number"
    step="0.01"
    name="yield"
    placeholder="ðŸŒ± Yield (tons)"
    value={formData.yield}
    onChange={handleChange}
    style={inputStyle}
  />
  <input
    type="number"
    step="0.01"
    name="cost"
    placeholder="ðŸ’° Total Cost (â‚¹)"
    value={formData.cost}
    onChange={handleChange}
    style={inputStyle}
  />
  <input
    type="number"
    step="0.01"
    name="pricePerTon"
    placeholder="ðŸ·ï¸ Price per Ton (â‚¹)"
    value={formData.pricePerTon}
    onChange={handleChange}
    style={inputStyle}
  />

  <button
    type="submit"
    style={{
      gridColumn: "1 / -1",
      padding: "10px 20px",
      backgroundColor: "#2E7D32",
      color: "white",
      fontWeight: "bold",
      border: "none",
      borderRadius: "8px",
      cursor: "pointer",
      transition: "all 0.3s ease",
    }}
    onMouseOver={(e) => (e.target.style.backgroundColor = "#1B5E20")}
    onMouseOut={(e) => (e.target.style.backgroundColor = "#2E7D32")}
  >
    âž• Add Record
  </button>
</form>


      {records.length > 0 ? (
        <>
          <Bar data={chartData} />
          <table
            style={{
              width: "100%",
              marginTop: "20px",
              borderCollapse: "collapse",
            }}
          >
            <thead>
              <tr style={{ background: "#e0e0e0" }}>
                <th>Year</th>
                <th>Crop</th>
                <th>Yield (tons)</th>
                <th>Revenue (â‚¹)</th>
                <th>Cost (â‚¹)</th>
                <th>Profit/Loss (â‚¹)</th>
              </tr>
            </thead>
            <tbody>
              {records.map((r, index) => (
                <tr key={index}>
                  <td>{r.year}</td>
                  <td>{r.crop}</td>
                  <td>{r.yield}</td>
                  <td>{r.revenue}</td>
                  <td>{r.cost}</td>
                  <td style={{ color: r.profit >= 0 ? "green" : "red" }}>
                    {r.profit}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p>No data added yet.</p>
      )}
    </div>
  );
}

const btnStyle = {
  gridColumn: "span 5",
  padding: "8px 16px",
  backgroundColor: "#2E7D32",
  color: "white",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
};
const inputStyle = {
  padding: "10px 12px",
  borderRadius: "6px",
  border: "1px solid #ccc",
  outline: "none",
  fontSize: "14px",
  transition: "border-color 0.3s ease, box-shadow 0.3s ease",
  boxShadow: "0 1px 3px rgba(0, 0, 0, 0.05)",
};



export default ProfitLossPage;
