import React from "react";
import { Link, useLocation } from "react-router-dom";

function Header() {
  const location = useLocation();

  const linkStyle = (path) => ({
    margin: "0 15px",
    textDecoration: "none",
    color: location.pathname === path ? "#2E7D32" : "black",
    fontWeight: location.pathname === path ? "bold" : "normal",
  });

  return (
    <header style={headerStyle}>
      <h2 style={{ color: "#2E7D32" }}>üåæ AI Crop Yield Predictor</h2>
      <nav>
        <Link to="/" style={linkStyle("/")}>üè† Home</Link>
        {/* <Link to="/result" style={linkStyle("/result")}>üìä Result</Link> */}
        <Link to="/history" style={linkStyle("/history")}>üìú History</Link>
        <Link to="/dashboard" style={linkStyle("/dashboard")}>üìà Dashboard</Link>

      </nav>
    </header>
  );
}

const headerStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  background: "#f0f0f0",
  padding: "10px 20px",
  boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  marginBottom: "20px"
};

export default Header;
