import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function DashboardPage() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("predictionHistory")) || [];
    setHistory(saved);
  }, []);

  if (history.length === 0) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h2>No data yet üìâ</h2>
        <p>Go to the prediction form and generate some results first!</p>
      </div>
    );
  }

  // üìä Prepare data for chart
  const labels = history.map((item) => item.Crop + " (" + item.Year + ")");
  const yields = history.map((item) => item.predicted_yield);

  const chartData = {
    labels,
    datasets: [
      {
        label: "Predicted Yield (tons/hectare)",
        data: yields,
        backgroundColor: "rgba(46, 125, 50, 0.6)",
        borderColor: "#2E7D32",
        borderWidth: 1,
      },
    ],
  };

  // üìà Calculate summary stats
  const avgRainfall =
    history.reduce((sum, item) => sum + item.Rainfall, 0) / history.length;
  const avgTemp =
    history.reduce((sum, item) => sum + item.Avg_Temperature, 0) / history.length;

  const mostCommonCrop = Object.entries(
    history.reduce((acc, item) => {
      acc[item.Crop] = (acc[item.Crop] || 0) + 1;
      return acc;
    }, {})
  ).sort((a, b) => b[1] - a[1])[0][0];

  return (
    <div style={{ maxWidth: "800px", margin: "50px auto", padding: "20px" }}>
      <h2>üìä Yield Dashboard</h2>
      <Bar data={chartData} />

      <div style={summaryContainer}>
        <div style={card}><h3>üåæ Common Crop</h3><p>{mostCommonCrop}</p></div>
        <div style={card}><h3>üåßÔ∏è Avg Rainfall</h3><p>{avgRainfall.toFixed(1)} mm</p></div>
        <div style={card}><h3>üå°Ô∏è Avg Temperature</h3><p>{avgTemp.toFixed(1)} ¬∞C</p></div>
      </div>
      <button onClick={() => navigate("/schemes")} style={btnStyle}>
  üåø Government Schemes
</button>

    </div>
  );
}

const summaryContainer = {
  display: "flex",
  justifyContent: "space-around",
  marginTop: "40px",
};

const card = {
  background: "#f4f6f8",
  borderRadius: "8px",
  padding: "20px",
  boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
  textAlign: "center",
  width: "200px",
};

export default DashboardPage;
