import React, { useEffect, useState } from "react";

function HistoryPage() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("predictionHistory")) || [];
    setHistory(saved);
  }, []);

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h2 style={styles.heading}>ðŸ“Š Prediction History</h2>

        {history.length === 0 ? (
          <div style={styles.noData}>
            ðŸŒ± No predictions yet! <br /> Go to the Predict Yield page and submit a form.
          </div>
        ) : (
          <div style={styles.tableContainer}>
            <table style={styles.table}>
              <thead style={styles.thead}>
                <tr>
                  <th>Crop</th>
                  <th>Soil pH</th>
                  <th>Rainfall (mm)</th>
                  <th>Temperature (Â°C)</th>
                  <th>Predicted Yield (tons/ha)</th>
                </tr>
              </thead>
              <tbody>
                {history.map((item, idx) => (
                  <tr
                    key={idx}
                    style={idx % 2 === 0 ? styles.rowEven : styles.rowOdd}
                  >
                    <td style={styles.cell}>{item.Crop}</td>
                    <td style={styles.cell}>{item.Soil_pH}</td>
                    <td style={styles.cell}>{item.Rainfall}</td>
                    <td style={styles.cell}>{item.Avg_Temperature}</td>
                    <td style={styles.cell}>{item.predicted_yield}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "0vh",
    padding: "50px 20px",
    display: "flex",
    justifyContent: "center",
    background: "#f6fff5",
    fontFamily: "'Poppins', sans-serif",
  },

  card: {
    width: "100%",
    maxWidth: "1000px",
    background: "#ffffff",
    borderRadius: "20px",
    padding: "40px",
    boxShadow: "0 8px 20px rgba(0,0,0,0.08)",
  },

  heading: {
    textAlign: "center",
    color: "#2E7D32",
    fontSize: "28px",
    marginBottom: "30px",
    fontWeight: "700",
  },

  noData: {
    textAlign: "center",
    color: "#1B5E20",
    fontSize: "18px",
    padding: "25px",
    borderRadius: "15px",
    background: "rgba(200, 230, 201, 0.3)",
    fontWeight: "600",
  },

  tableContainer: {
    overflowX: "auto",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
  },

  table: {
    width: "100%",
    borderCollapse: "separate",
    borderSpacing: "0 10px",
    fontSize: "18px", // bigger font for readability
    textAlign: "left",
  },

  thead: {
    backgroundColor: "#C8E6C9",
    fontWeight: "700",
    fontSize: "17px",
  },

  rowEven: {
    backgroundColor: "#ffffff",
    borderBottom: "1px solid #d0d0d0",
  },

  rowOdd: {
    backgroundColor: "#f0fff0",
    borderBottom: "1px solid #d0d0d0",
  },

  cell: {
    padding: "12px 10px", // more padding for better readability
    fontSize: "17px", // make digits bigger
  },
};

export default HistoryPage;
