import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function YieldForm() {
  const [formData, setFormData] = useState({
    Field_ID: 102,
    Year: 2024,
    Crop: "Maize",
    Previous_Crop: "Wheat",
    Soil_pH: 6.5,
    Soil_Nitrogen: 50,
    Soil_Phosphorus: 20,
    Soil_Potassium: 140,
    Organic_Matter: 3.5,
    Rainfall: 600,
    Avg_Temperature: 25,
    NDVI: 0.65,
    Irrigation: "Drip",
    Fertilizer_Type: "Chemical",
  });

  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const cropOptions = ["Wheat", "Maize", "Soybean", "Rice", "Barley", "Legume"];
  const irrigationOptions = ["None", "Drip", "Sprinkler"];
  const fertilizerOptions = ["None", "Chemical", "Organic"];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]:
        ["Field_ID", "Year", "Soil_Nitrogen", "Soil_Phosphorus", "Soil_Potassium", "Rainfall"].includes(name)
          ? parseInt(value)
          : ["Soil_pH", "Avg_Temperature", "Organic_Matter", "NDVI"].includes(name)
          ? parseFloat(value)
          : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://127.0.0.1:8000/predict", formData);
      const predicted_yield = response.data.predicted_yield;

      const resultData = { ...formData, predicted_yield };
      navigate("/result", { state: { result: resultData } });
    } catch (error) {
      console.error("Error predicting yield:", error);
      setError("‚ö†Ô∏è Error occurred while predicting yield!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.page}>
      <div style={styles.formCard}>
        <h2 style={styles.heading}>üåæ AI Crop Yield Prediction</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          {[
            "Field_ID",
            "Year",
            "Soil_pH",
            "Soil_Nitrogen",
            "Soil_Phosphorus",
            "Soil_Potassium",
            "Organic_Matter",
            "Rainfall",
            "Avg_Temperature",
            "NDVI",
          ].map((field) => (
            <div key={field} style={styles.inputGroup}>
              <label style={styles.label}>{field.replace("_", " ")}:</label>
              <input
                type="number"
                step={["Soil_pH", "Organic_Matter", "Avg_Temperature", "NDVI"].includes(field) ? "0.01" : "1"}
                name={field}
                value={formData[field]}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
          ))}

          {/* Dropdowns */}
          <div style={styles.inputGroup}>
            <label style={styles.label}>Crop:</label>
            <select name="Crop" value={formData.Crop} onChange={handleChange} style={styles.select}>
              {cropOptions.map((crop) => (
                <option key={crop} value={crop}>
                  {crop}
                </option>
              ))}
            </select>
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Previous Crop:</label>
            <select name="Previous_Crop" value={formData.Previous_Crop} onChange={handleChange} style={styles.select}>
              {cropOptions.map((crop) => (
                <option key={crop} value={crop}>
                  {crop}
                </option>
              ))}
            </select>
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Irrigation:</label>
            <select name="Irrigation" value={formData.Irrigation} onChange={handleChange} style={styles.select}>
              {irrigationOptions.map((opt) => (
                <option key={opt} value={opt}>
                  {opt}
                </option>
              ))}
            </select>
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Fertilizer Type:</label>
            <select name="Fertilizer_Type" value={formData.Fertilizer_Type} onChange={handleChange} style={styles.select}>
              {fertilizerOptions.map((opt) => (
                <option key={opt} value={opt}>
                  {opt}
                </option>
              ))}
            </select>
          </div>

          <button type="submit" style={styles.button} disabled={loading}>
            {loading ? "‚è≥ Predicting..." : "üå± Predict Yield"}
          </button>
        </form>

        {prediction && (
          <h3 style={styles.result}>Predicted Yield: {prediction} tons/ha</h3>
        )}
        {error && <p style={styles.error}>{error}</p>}
      </div>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    background: `linear-gradient(rgba(255,255,255,0.85), rgba(255,255,255,0.85)), url('https://png.pngtree.com/background/20230528/original/pngtree-green-forest-background-with-leaves-picture-image_2788646.jpg') center/cover no-repeat`,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    padding: "40px 20px",
    fontFamily: "'Poppins', sans-serif",
  },

  formCard: {
    background: "white",
    borderRadius: "20px",
    boxShadow: "0 8px 25px rgba(0,0,0,0.15)",
    padding: "40px",
    width: "100%",
    maxWidth: "650px",
    animation: "fadeIn 0.8s ease-in-out",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
  },

  heading: {
    textAlign: "center",
    color: "#2E7D32",
    marginBottom: "25px",
    fontSize: "26px",
  },

  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },

  inputGroup: {
    display: "flex",
    flexDirection: "column",
  },

  label: {
    fontWeight: "bold",
    marginBottom: "5px",
    color: "#333",
  },

  input: {
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    outline: "none",
    transition: "0.3s",
    fontSize: "15px",
  },

  select: {
    padding: "10px",
    borderRadius: "10px",
    border: "1px solid #ccc",
    fontSize: "15px",
    transition: "0.3s",
  },

  button: {
    marginTop: "15px",
    padding: "12px 20px",
    background: "linear-gradient(135deg, #4CAF50, #2E7D32)",
    color: "white",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    fontWeight: "bold",
    fontSize: "16px",
    transition: "0.3s",
  },

  result: {
    textAlign: "center",
    marginTop: "25px",
    fontSize: "18px",
    color: "#1B5E20",
  },

  error: {
    color: "red",
    textAlign: "center",
    marginTop: "15px",
  },
};

// CSS Animation
const styleSheet = document.styleSheets[0];
styleSheet.insertRule(`
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(30px); }
  to { opacity: 1; transform: translateY(0); }
}`, styleSheet.cssRules.length);

export default YieldForm;
