import React, { useEffect, useState } from "react";

function HistoryPage() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("predictionHistory")) || [];
    setHistory(saved);
  }, []);

  return (
    <div style={styles.page}>
      <div style={styles.glassCard}>
        <h2 style={styles.heading}>ðŸ“Š Prediction History</h2>

        {history.length === 0 ? (
          <p style={styles.noData}>
            ðŸŒ± No predictions yet! Go to the Predict Yield page and submit a form.
          </p>
        ) : (
          <div style={styles.tableWrapper}>
            <table style={styles.table}>
              <thead>
                <tr>
                  <th>Crop</th>
                  <th>Soil pH</th>
                  <th>Rainfall (mm)</th>
                  <th>Temperature (Â°C)</th>
                  <th>Predicted Yield (tons/ha)</th>
                </tr>
              </thead>
              <tbody>
                {history.map((item, index) => (
                  <tr key={index} style={index % 2 === 0 ? styles.rowEven : styles.rowOdd}>
                    <td>{item.Crop}</td>
                    <td>{item.Soil_pH}</td>
                    <td>{item.Rainfall}</td>
                    <td>{item.Avg_Temperature}</td>
                    <td>{item.predicted_yield}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    padding: "40px 20px",
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    background: `linear-gradient(120deg, rgba(230, 255, 230, 0.9), rgba(250, 255, 245, 0.9)), 
                 url('https://images.unsplash.com/photo-1556761175-129418cb2dfe?auto=format&fit=crop&w=1600&q=80') center/cover`,
    fontFamily: "'Poppins', sans-serif",
  },

  glassCard: {
    background: "rgba(255, 255, 255, 0.65)",
    padding: "30px",
    borderRadius: "20px",
    width: "100%",
    maxWidth: "900px",
    boxShadow: "0 12px 30px rgba(0,0,0,0.15)",
    backdropFilter: "blur(15px)",
    transition: "all 0.3s ease",
  },

  heading: {
    textAlign: "center",
    color: "#2E7D32",
    fontSize: "28px",
    marginBottom: "20px",
    fontWeight: "700",
  },

  noData: {
    textAlign: "center",
    color: "#1B5E20",
    fontSize: "18px",
    marginTop: "30px",
    fontWeight: "500",
  },

  tableWrapper: {
    overflowX: "auto",
  },

  table: {
    width: "100%",
    borderCollapse: "collapse",
    textAlign: "center",
    minWidth: "700px",
  },

  rowEven: {
    background: "rgba(200, 230, 201, 0.4)",
    transition: "0.3s",
  },

  rowOdd: {
    background: "rgba(220, 245, 224, 0.4)",
    transition: "0.3s",
  },
};

export default HistoryPage;
