import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import { useNavigate } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function ProfitLossPage() {
  const [records, setRecords] = useState([]);
  const [formData, setFormData] = useState({
    year: "",
    crop: "",
    yield: "",
    cost: "",
    pricePerTon: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    const { year, crop, yield: y, cost, pricePerTon } = formData;

    if (!year || !crop || !y || !cost || !pricePerTon) {
      alert("Please fill all fields!");
      return;
    }

    const yieldValue = parseFloat(y);
    const price = parseFloat(pricePerTon);
    const costValue = parseFloat(cost);
    const revenue = yieldValue * price;
    const profit = revenue - costValue;

    const newRecord = { ...formData, revenue, profit };
    setRecords([...records, newRecord]);
    setFormData({ year: "", crop: "", yield: "", cost: "", pricePerTon: "" });
  };

  const totalProfit = records.reduce((acc, rec) => acc + rec.profit, 0);

  const chartData = {
    labels: records.map((r) => r.year),
    datasets: [
      {
        label: "Profit (‚Çπ)",
        data: records.map((r) => r.profit),
        backgroundColor: records.map((r) =>
          r.profit >= 0 ? "rgba(102, 187, 106, 0.7)" : "rgba(239, 83, 80, 0.7)"
        ),
        borderRadius: 8,
      },
    ],
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>üíπ Profit‚ÄìLoss Analysis</h2>

      <form onSubmit={handleAdd} style={styles.form}>
        {["year", "crop", "yield", "cost", "pricePerTon"].map((field, i) => (
          <input
            key={i}
            type={field === "crop" ? "text" : "number"}
            step="0.01"
            name={field}
            placeholder={
              field === "year"
                ? "Year"
                : field === "yield"
                ? "Yield (tons)"
                : field === "cost"
                ? "Total Cost (‚Çπ)"
                : field === "pricePerTon"
                ? "Price per Ton (‚Çπ)"
                : field
            }
            value={formData[field]}
            onChange={handleChange}
            style={styles.input}
          />
        ))}
        <button type="submit" style={styles.addBtn}>
          ‚ûï Add Record
        </button>
      </form>

      {records.length > 0 ? (
        <>
          <div style={styles.chartContainer}>
            <Bar data={chartData} />
          </div>

          <h3 style={styles.totalProfit}>
            Total Profit: <span>‚Çπ{totalProfit.toFixed(2)}</span>
          </h3>

          <button
            onClick={() => navigate("/rewards", { state: { totalProfit } })}
            style={styles.rewardBtn}
          >
            üéÅ View My Rewards
          </button>

          <div style={styles.tableWrapper}>
            <table style={styles.table}>
              <thead>
                <tr>
                  <th>Year</th>
                  <th>Crop</th>
                  <th>Yield (tons)</th>
                  <th>Revenue (‚Çπ)</th>
                  <th>Cost (‚Çπ)</th>
                  <th>Profit/Loss (‚Çπ)</th>
                </tr>
              </thead>
              <tbody>
                {records.map((r, i) => (
                  <tr key={i}>
                    <td>{r.year}</td>
                    <td>{r.crop}</td>
                    <td>{r.yield}</td>
                    <td>{r.revenue}</td>
                    <td>{r.cost}</td>
                    <td style={{ color: r.profit >= 0 ? "#2E7D32" : "#E53935" }}>
                      {r.profit}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      ) : (
        <p style={{ marginTop: "20px", color: "#666" }}>No records added yet. Start by adding one! üå±</p>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "950px",
     minHeight: "30vh",
    margin: "40px auto",
    textAlign: "center",
    background: "linear-gradient(180deg, #F1F8E9, #ffffff)",
    padding: "40px",
    borderRadius: "16px",
    boxShadow: "0 6px 16px rgba(0,0,0,0.1)",
    transition: "all 0.4s ease",
  },
  heading: {
    color: "#2E7D32",
    marginBottom: "20px",
    fontSize: "26px",
    fontFamily: "'Poppins', sans-serif",
  },
  form: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(150px, 1fr))",
    gap: "12px",
    marginBottom: "25px",
  },
  input: {
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #A5D6A7",
    outline: "none",
    fontFamily: "'Poppins', sans-serif",
    transition: "0.3s",
  },
  addBtn: {
    gridColumn: "1 / -1",
    padding: "12px",
    background: "linear-gradient(90deg, #66BB6A, #81C784)",
    color: "white",
    fontWeight: "bold",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    transition: "0.3s ease",
  },
  chartContainer: {
    background: "#FAFAFA",
    padding: "20px",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.08)",
    marginTop: "20px",
  },
  totalProfit: {
    marginTop: "25px",
    color: "#1B5E20",
    fontSize: "20px",
    fontWeight: "600",
  },
  rewardBtn: {
    marginTop: "20px",
    background: "linear-gradient(90deg, #FFB74D, #FFA726)",
    color: "white",
    padding: "12px 25px",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
    transition: "all 0.3s ease",
  },
  tableWrapper: {
    marginTop: "30px",
    overflowX: "auto",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    fontFamily: "'Poppins', sans-serif",
  },
  th: {
    background: "#C8E6C9",
  },
};

export default ProfitLossPage;
