import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function ProfitLossPage() {
  const [records, setRecords] = useState([]);
  const [formData, setFormData] = useState({
    year: "",
    crop: "",
    yield: "",
    cost: "",
    pricePerTon: "",
  });

  // handle form changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // add record
  const handleAdd = (e) => {
    e.preventDefault();
    if (!formData.year || !formData.crop || !formData.yield || !formData.cost || !formData.pricePerTon) {
      alert("âš ï¸ Please fill all fields!");
      return;
    }

    const yieldValue = parseFloat(formData.yield);
    const price = parseFloat(formData.pricePerTon);
    const cost = parseFloat(formData.cost);
    const revenue = yieldValue * price;
    const profit = revenue - cost;

    const newRecord = {
      ...formData,
      revenue,
      profit,
    };

    setRecords([...records, newRecord]);
    setFormData({ year: "", crop: "", yield: "", cost: "", pricePerTon: "" });
  };

  // chart data
  const chartData = {
    labels: records.map((r) => r.year),
    datasets: [
      {
        label: "Profit/Loss (â‚¹)",
        data: records.map((r) => r.profit),
        backgroundColor: records.map((r) =>
          r.profit >= 0 ? "rgba(34,197,94,0.7)" : "rgba(239,68,68,0.7)"
        ),
        borderRadius: 6,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { display: true, position: "top" },
      title: { display: true, text: "ðŸ“Š Yearly Profitâ€“Loss Comparison" },
    },
    animation: { duration: 1200 },
  };

  return (
    <div
      style={{
        maxWidth: "900px",
        margin: "50px auto",
        padding: "30px",
        borderRadius: "12px",
        backgroundColor: "#f8fafc",
        boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
      }}
    >
      <h2 style={{ color: "#2E7D32", textAlign: "center", marginBottom: "25px" }}>
        ðŸ’¹ Profitâ€“Loss Analysis Dashboard
      </h2>

      <form
        onSubmit={handleAdd}
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(150px, 1fr))",
          gap: "10px",
          marginBottom: "20px",
        }}
      >
        <input type="number" name="year" placeholder="Year" value={formData.year} onChange={handleChange} style={inputStyle} />
        <input type="text" name="crop" placeholder="Crop" value={formData.crop} onChange={handleChange} style={inputStyle} />
        <input type="number" step="0.01" name="yield" placeholder="Yield (tons)" value={formData.yield} onChange={handleChange} style={inputStyle} />
        <input type="number" step="0.01" name="cost" placeholder="Total Cost (â‚¹)" value={formData.cost} onChange={handleChange} style={inputStyle} />
        <input type="number" step="0.01" name="pricePerTon" placeholder="Price per Ton (â‚¹)" value={formData.pricePerTon} onChange={handleChange} style={inputStyle} />

        <button type="submit" style={btnStyle}>
          âž• Add Record
        </button>
      </form>

      {records.length > 0 ? (
        <>
          <div style={{ background: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 3px 10px rgba(0,0,0,0.1)" }}>
            <Bar data={chartData} options={chartOptions} />
          </div>

          <h3 style={{ marginTop: "30px", color: "#1e3a8a" }}>ðŸ“… Detailed Records</h3>
          <table
            style={{
              width: "100%",
              marginTop: "10px",
              borderCollapse: "collapse",
              backgroundColor: "white",
              borderRadius: "10px",
              overflow: "hidden",
              boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
            }}
          >
            <thead style={{ background: "#e0e7ff" }}>
              <tr>
                <th>Year</th>
                <th>Crop</th>
                <th>Yield (tons)</th>
                <th>Revenue (â‚¹)</th>
                <th>Cost (â‚¹)</th>
                <th>Profit/Loss (â‚¹)</th>
              </tr>
            </thead>
            <tbody>
              {records.map((r, index) => (
                <tr key={index}>
                  <td>{r.year}</td>
                  <td>{r.crop}</td>
                  <td>{r.yield}</td>
                  <td>{r.revenue.toFixed(2)}</td>
                  <td>{r.cost.toFixed(2)}</td>
                  <td style={{ color: r.profit >= 0 ? "green" : "red", fontWeight: "bold" }}>
                    {r.profit.toFixed(2)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p style={{ color: "#6b7280" }}>No records added yet. Start by entering data above.</p>
      )}
    </div>
  );
}

const inputStyle = {
  padding: "8px 10px",
  border: "1px solid #ccc",
  borderRadius: "6px",
  fontSize: "14px",
};

const btnStyle = {
  gridColumn: "1 / -1",
  padding: "10px",
  backgroundColor: "#2563eb",
  color: "white",
  fontWeight: "bold",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
  transition: "background 0.3s",
};

export default ProfitLossPage;
