import React from "react";
import { Link, useLocation } from "react-router-dom";

function Header() {
  const location = useLocation();

  const linkStyle = (path) => ({
    margin: "0 18px",
    textDecoration: "none",
    color: location.pathname === path ? "#1B5E20" : "#333",
    fontWeight: location.pathname === path ? "bold" : "500",
    position: "relative",
    transition: "color 0.3s ease, transform 0.3s ease",
  });

  const underlineStyle = (path) => ({
    content: "''",
    position: "absolute",
    left: 0,
    bottom: "-5px",
    height: "2px",
    width: location.pathname === path ? "100%" : "0%",
    backgroundColor: "#2E7D32",
    transition: "width 0.3s ease",
  });

  return (
    <header style={headerStyle}>
      <div style={logoContainer}>
        <span style={logoEmoji}>ðŸŒ¾</span>
        <h2 style={logoText}>AI Crop Yield Predictor</h2>
      </div>

      <nav style={navStyle}>
        {[
          { to: "/", label: "ðŸ  Home" },
          { to: "/history", label: "ðŸ“œ History" },
          { to: "/dashboard", label: "ðŸ“ˆ Dashboard" },
          { to: "/schemes", label: "ðŸŒ± Schemes" },
          { to: "/profit-loss", label: "ðŸ’° Profit/Loss" },
        ].map((item) => (
          <div key={item.to} style={{ position: "relative", display: "inline-block" }}>
            <Link
              to={item.to}
              style={linkStyle(item.to)}
              onMouseEnter={(e) => (e.target.style.color = "#2E7D32")}
              onMouseLeave={(e) =>
                (e.target.style.color =
                  location.pathname === item.to ? "#1B5E20" : "#333")
              }
            >
              {item.label}
            </Link>
            <span style={underlineStyle(item.to)}></span>
          </div>
        ))}
      </nav>
    </header>
  );
}

const headerStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  background: "linear-gradient(90deg, #E8F5E9, #C8E6C9)",
  padding: "15px 40px",
  boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
  position: "sticky",
  top: 0,
  zIndex: 1000,
  borderBottom: "2px solid #A5D6A7",
  transition: "all 0.4s ease",
};

const logoContainer = {
  display: "flex",
  alignItems: "center",
  gap: "10px",
};

const logoEmoji = {
  fontSize: "28px",
  transition: "transform 0.3s ease",
};

const logoText = {
  color: "#2E7D32",
  fontSize: "22px",
  fontWeight: "700",
  letterSpacing: "0.5px",
  fontFamily: "'Poppins', sans-serif",
};

const navStyle = {
  display: "flex",
  alignItems: "center",
  fontFamily: "'Poppins', sans-serif",
  fontSize: "16px",
};

export default Header;
