import React, { useState } from "react";
import axios from "axios";

function YieldForm() {
  const [formData, setFormData] = useState({
    Field_ID: "",
    Year: "",
    Crop: "",
    Previous_Crop: "",
    Soil_pH: "",
    Soil_Nitrogen: "",
    Soil_Phosphorus: "",
    Soil_Potassium: "",
    Organic_Matter: "",
    Rainfall: "",
    Avg_Temperature: "",
    NDVI: "",
    Irrigation: "",
    Fertilizer_Type: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://127.0.0.1:8000/predict", formData);
      const predicted_yield = response.data.predicted_yield;

      // ‚úÖ Save to localStorage
      const history = JSON.parse(localStorage.getItem("predictionHistory")) || [];
      const newEntry = { ...formData, predicted_yield };
      history.push(newEntry);
      localStorage.setItem("predictionHistory", JSON.stringify(history));

      alert(`‚úÖ Predicted Yield: ${predicted_yield.toFixed(2)} tons/hectare`);
    } catch (error) {
      console.error("Error predicting yield:", error);
      alert("‚ùå Error occurred while predicting yield!");
    }
  };

  return (
    <div style={{ maxWidth: "700px", margin: "50px auto", padding: "20px" }}>
      <h2>üåæ AI Crop Yield Prediction</h2>
      <form onSubmit={handleSubmit}>
        {/* Basic Field Info */}
        <div className="form-group">
          <label>Field ID</label>
          <input
            type="number"
            name="Field_ID"
            value={formData.Field_ID}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Year</label>
          <input
            type="number"
            name="Year"
            value={formData.Year}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Crop</label>
          <select name="Crop" value={formData.Crop} onChange={handleChange} required>
            <option value="">Select Crop</option>
            <option value="Wheat">Wheat</option>
            <option value="Rice">Rice</option>
            <option value="Maize">Maize</option>
            <option value="Sugarcane">Sugarcane</option>
          </select>
        </div>

        <div className="form-group">
          <label>Previous Crop</label>
          <select
            name="Previous_Crop"
            value={formData.Previous_Crop}
            onChange={handleChange}
            required
          >
            <option value="">Select Previous Crop</option>
            <option value="Wheat">Wheat</option>
            <option value="Rice">Rice</option>
            <option value="Maize">Maize</option>
            <option value="Sugarcane">Sugarcane</option>
          </select>
        </div>

        {/* Soil Data */}
        <div className="form-group">
          <label>Soil pH</label>
          <input
            type="number"
            step="0.1"
            name="Soil_pH"
            value={formData.Soil_pH}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Nitrogen (mg/kg)</label>
          <input
            type="number"
            name="Soil_Nitrogen"
            value={formData.Soil_Nitrogen}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Phosphorus (mg/kg)</label>
          <input
            type="number"
            name="Soil_Phosphorus"
            value={formData.Soil_Phosphorus}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Potassium (mg/kg)</label>
          <input
            type="number"
            name="Soil_Potassium"
            value={formData.Soil_Potassium}
            onChange={handleChange}
            required
          />
        </div>

        {/* Environmental Factors */}
        <div className="form-group">
          <label>Organic Matter (%)</label>
          <input
            type="number"
            step="0.1"
            name="Organic_Matter"
            value={formData.Organic_Matter}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Rainfall (mm)</label>
          <input
            type="number"
            name="Rainfall"
            value={formData.Rainfall}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Average Temperature (¬∞C)</label>
          <input
            type="number"
            step="0.1"
            name="Avg_Temperature"
            value={formData.Avg_Temperature}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>NDVI (0 - 1)</label>
          <input
            type="number"
            step="0.01"
            name="NDVI"
            value={formData.NDVI}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Irrigation Type</label>
          <select
            name="Irrigation"
            value={formData.Irrigation}
            onChange={handleChange}
            required
          >
            <option value="">Select</option>
            <option value="Drip">Drip</option>
            <option value="Sprinkler">Sprinkler</option>
            <option value="Flood">Flood</option>
          </select>
        </div>

        <div className="form-group">
          <label>Fertilizer Type</label>
          <select
            name="Fertilizer_Type"
            value={formData.Fertilizer_Type}
            onChange={handleChange}
            required
          >
            <option value="">Select</option>
            <option value="Organic">Organic</option>
            <option value="Chemical">Chemical</option>
            <option value="Mixed">Mixed</option>
          </select>
        </div>

        <button type="submit" style={btnStyle}>
          Predict Yield
        </button>
      </form>
    </div>
  );
}

const btnStyle = {
  backgroundColor: "#4CAF50",
  color: "white",
  padding: "10px 20px",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  marginTop: "20px",
};

export default YieldForm;
