import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import { useNavigate } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function ProfitLossPage() {
  const [records, setRecords] = useState([]);
  const [formData, setFormData] = useState({
    year: "",
    crop: "",
    yield: "",
    cost: "",
    pricePerTon: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    if (!formData.year || !formData.crop || !formData.yield || !formData.cost || !formData.pricePerTon) {
      alert("Please fill all fields!");
      return;
    }

    const yieldValue = parseFloat(formData.yield);
    const price = parseFloat(formData.pricePerTon);
    const cost = parseFloat(formData.cost);
    const revenue = yieldValue * price;
    const profit = revenue - cost;

    const newRecord = { ...formData, revenue, profit };
    setRecords([...records, newRecord]);
    setFormData({ year: "", crop: "", yield: "", cost: "", pricePerTon: "" });
  };

  const totalProfit = records.reduce((acc, rec) => acc + rec.profit, 0);

  const chartData = {
    labels: records.map((r) => r.year),
    datasets: [
      {
        label: "Profit (‚Çπ)",
        data: records.map((r) => r.profit),
        backgroundColor: records.map((r) =>
          r.profit >= 0 ? "rgba(76, 175, 80, 0.7)" : "rgba(244, 67, 54, 0.7)"
        ),
      },
    ],
  };

  return (
    <div style={styles.container}>
      <h2>üíπ Profit‚ÄìLoss Analysis</h2>

      <form onSubmit={handleAdd} style={styles.form}>
        <input type="number" name="year" placeholder="Year" value={formData.year} onChange={handleChange} />
        <input type="text" name="crop" placeholder="Crop" value={formData.crop} onChange={handleChange} />
        <input type="number" step="0.01" name="yield" placeholder="Yield (tons)" value={formData.yield} onChange={handleChange} />
        <input type="number" step="0.01" name="cost" placeholder="Total Cost (‚Çπ)" value={formData.cost} onChange={handleChange} />
        <input type="number" step="0.01" name="pricePerTon" placeholder="Price per Ton (‚Çπ)" value={formData.pricePerTon} onChange={handleChange} />
        <button type="submit" style={styles.addBtn}>‚ûï Add</button>
      </form>

      {records.length > 0 ? (
        <>
          <Bar data={chartData} />
          <h3 style={{ marginTop: "20px", color: "#2E7D32" }}>Total Profit: ‚Çπ{totalProfit.toFixed(2)}</h3>

          <button
            onClick={() => navigate("/rewards", { state: { totalProfit } })}
            style={styles.rewardBtn}
          >
            üéÅ View My Rewards
          </button>

          <table style={styles.table}>
            <thead>
              <tr style={{ background: "#e0e0e0" }}>
                <th>Year</th>
                <th>Crop</th>
                <th>Yield (tons)</th>
                <th>Revenue (‚Çπ)</th>
                <th>Cost (‚Çπ)</th>
                <th>Profit/Loss (‚Çπ)</th>
              </tr>
            </thead>
            <tbody>
              {records.map((r, i) => (
                <tr key={i}>
                  <td>{r.year}</td>
                  <td>{r.crop}</td>
                  <td>{r.yield}</td>
                  <td>{r.revenue}</td>
                  <td>{r.cost}</td>
                  <td style={{ color: r.profit >= 0 ? "green" : "red" }}>{r.profit}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p>No data added yet.</p>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "900px",
    margin: "50px auto",
    textAlign: "center",
    background: "#fff",
    padding: "30px",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
  },
  form: {
    display: "grid",
    gridTemplateColumns: "repeat(5, 1fr)",
    gap: "10px",
    marginBottom: "20px",
  },
  addBtn: {
    gridColumn: "span 5",
    padding: "10px",
    backgroundColor: "#2E7D32",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
  },
  rewardBtn: {
    marginTop: "20px",
    backgroundColor: "#FF9800",
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "16px",
  },
  table: {
    width: "100%",
    marginTop: "20px",
    borderCollapse: "collapse",
  },
};

export default ProfitLossPage;
